  }

  return (
    <>
      <Navbar/>
      <main className='container mt-5'>
        <ProductList productList = {productList} incrementQuantity = {incrementQuantity} decrementQuantity = {decrementQuantity}/>
      </main>
      <Footer productList = {productList} Reset = {Reset}/>
    </>
  );
}

export default App;
